mapper.select:
select *  from orders o,area a  where payment='已付款' and o.oArea=a.aId
and  date(o.oDate) <=last_day(curdate())
and date(o.oDate) >=(select date_add(curdate(), interval - day(curdate()) + 1 day))

and date(o.oDate)=#{oDate}

ORDER BY oDate desc
limit #{begin},#{count}

select count(o.oId) from orders o where payment='已付款' and date(o.oDate) <=last_day(curdate()) and date(o.oDate) >=(select date_add(curdate(), interval - day(curdate()) + 1 day))
and date(o.oDate)=#{oDate}
ORDER BY oDate desc

select o.odPrice,date(od.oDate) as oDates ,od.oDate,o.odName,od.oDesk,od.oyPrice,od.ocPrice,a.aName,od.oPrice,o.odNum
from orderdetails o inner join area a on o.odaid=a.aId inner join  orders od on od.oid=o.odoid
where  o.odoId=#{oId}
